package com.anders.vote.dao.impl;import org.junit.Test;import org.junit.runner.RunWith;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.test.annotation.Rollback;import org.springframework.test.context.ContextConfiguration;import org.springframework.test.context.junit4.SpringJUnit4ClassRunner;import org.springframework.transaction.annotation.Transactional;import com.anders.vote.bo.Tester;import com.anders.vote.dao.TesterDao;@RunWith(SpringJUnit4ClassRunner.class)@ContextConfiguration(locations = "classpath:applicationContext.xml")public class TesterDaoTest {	@Autowired	private TesterDao testerDao;	@Test	@Transactional	@Rollback(false)	public void test1() {		Tester tester = new Tester();		tester.setValue("123");		testerDao.save(tester);		tester.setValue("321");		testerDao.update(tester);	}}