mapper.select:
    SELECT 
	
    FROM td_url_whitelist 
    WHERE pos_id = #{posId} AND del_flag = 0
    
	AND domain = #{domain}
    
    
	AND c_type = #{cType}
    
    
	AND update_id = #{updateId}
    
    ORDER BY update_time DESC
    LIMIT #{start}, #{count}

    SELECT 
	COUNT(id)
    FROM td_url_whitelist 
    WHERE pos_id = #{posId} AND del_flag = 0
    
	AND domain = #{domain}
    
    
	AND c_type = #{cType}
    
    
	AND update_id = #{updateId}
