mapper.select:
    SELECT 
    
	FROM td_blacklist
	WHERE del_flag = 0
	
	AND company_name LIKE #{companyName}
    
	
	AND url LIKE #{url}
    
    
	AND creator_id = #{creatorId}
    
    
    AND src IN
		
			#{item}
		
    
    LIMIT #{start}, #{count}

    SELECT 
    COUNT(*)
	FROM td_blacklist
	WHERE del_flag = 0
	
	AND company_name LIKE #{companyName}
    
	
	AND url LIKE #{url}
    
    
	AND creator_id = #{creatorId}
    
    
    AND src IN
		
			#{item}

mapper.update:
UPDATE td_blacklist
SET del_flag = 1
WHERE blacklist_id = #{blacklistId}
